        public static List<Contact> UpdateFromDatabaseObjectList(List<b_Contact> dbObj, long userInfoId)
        {
            List<Contact> result = new List<Contact>();
            foreach (b_Contact contact in dbObj)
            {
                Contact tmp = new Contact();
                tmp.UpdateFromDatabaseObject(contact);
                tmp.IsEditable = (userInfoId == contact.OwnerId); // The contact can only be edited by the owner
                result.Add(tmp);
