            Contact_RetrieveByObjectId trans = new Contact_RetrieveByObjectId();
            trans.Contact = this.ToDatabaseObject();
            trans.dbKey = dbKey.ToTransDbKey();
            trans.Execute();
            contactList = UpdateFromDatabaseObjectList(trans.ContactList, dbKey.User.UserInfoId);

            return contactList;
        }

